import {useState, useEffect} from 'react'
import axios from 'axios'
import bannerService from './banners'
import Nav from './components/Nav'


const App = () => {

  const [sections, setSections] = useState([])

  useEffect(() => {
    bannerService.classSearch(202320, 'ISCO')
      .then(sec => setSections(sec.data.data))
  }, [])

  const backtrack = (a, k, input, solutions, credits, max_credits) => {
    if(is_a_solution(a, k, input, credits)) {
      //console.log("solution found")
      process_solution(a, k, input, solutions);
    } else {
      k = k + 1;
      let candidates = construct_candidates(a, k, input, credits, max_credits)
      //console.log(candidates)
      for (const candidate of candidates) {
        //make_move(a, k, input);
        a.push(candidate)
        let candidate_credits = (candidate.creditHours !== null ? candidate.creditHours : (candidate.creditHourHigh + 
          candidate.creditHourLow))
        credits += candidate_credits
        console.log(credits)
        input = candidates
        backtrack(a, k, input, solutions, credits, max_credits);
        a.pop(candidate)
        credits -= candidate_credits

      }
    }
  } 

  const is_a_solution = (a, k, input, credits) => (
    (input.length <= 1) && (credits >= 18)
    );

  const construct_candidates = (a, k, input, credits, max_credits) => {
    if (a.length === 0) {
      return input
    }
    else {
      return prune(input, a[k-2], credits, max_credits)
    }
  }  

  const process_solution = (a, k, input, solutions) => {
    solutions.push(JSON.parse(JSON.stringify(a)))
    console.log(solutions)
    }

  const generateSchedule = (input) => {
    let a = []
    let credits = 0
    let max_credits = 20
    let solutions = []
    const sections2 = JSON.parse(JSON.stringify(input));
    backtrack(a, 0, sections2, solutions, credits, max_credits)
    return solutions;
  } 

  const overlap = (sec1, sec2) => {
    const days = ["monday", "tuesday", "wednesday", "thursday", "friday", "saturday", "sunday"];
    for (const meetSec1 of sec1.meetingsFaculty) {
      for (const meetSec2 of sec2.meetingsFaculty) {
        for (const day of days) {
          if (meetSec1.meetingTime[day] && meetSec2.meetingTime[day]) {
            if (meetSec1.meetingTime.beginTime <= meetSec2.meetingTime.endTime
              && meetSec1.meetingTime.endTime >= meetSec2.meetingTime.beginTime) {
                return true
              }
            break  
          }
        }
      }
    }
    return false
  }



  const prune = (arr, section, credits, max_credits) => {
    return(
    arr.filter((sec) => (sec.subjectCourse !== section.subjectCourse) &&
    !overlap(sec, section) && (((sec.creditHours !== null ? sec.creditHours : (sec.creditHourHigh + 
      sec.creditHourLow)) + credits) <= max_credits)
    ))}

  if(sections.length > 1 ) {
    console.log("hola?")
    const sectionsxd = sections.filter((sec) => sec.subjectCourse === "ISCOA01A" || sec.subjectCourse === "ISCOA05A" || sec.subjectCourse === "ISCOC08A" || sec.subjectCourse === "ISCOZ01A" || sec.subjectCourse === "ISCOP01A" || sec.subjectCourse === "ISCOC11A")
    console.log("solutionss:", generateSchedule(sectionsxd))
  }

  return(
    <div class='par'>
      <Nav/>
      <ul>
        {sections.map(section => <li>{section.courseTitle}</li>)}
      </ul>
    </div>)
}

export default App;
