import {useState, useEffect} from 'react'
import axios from 'axios'
import bannerService from './banners'
import Nav from './components/Nav'


const App = () => {

  const [sections, setSections] = useState([])

  useEffect(() => {
    bannerService.classSearch(202320, 'ISCO')
      .then(sec => setSections(sec.data.data))
  }, [])

  const backtrack = (a, k, input, solutions, credits, max_credits) => {
    if(is_a_solution(a, k, input)) {
      //console.log("solution found")
      process_solution(a, k, input, solutions);
    } else {
      k = k + 1;
      let candidates = construct_candidates(a, k, input)
      for (const candidate of candidates) {
        //make_move(a, k, input);
        a.push(candidate)
        credits += (candidate.creditHours !== null ? candidate.creditHours : (candidate.creditHourHigh + 
        candidate.creditHourLow))
        backtrack(a, k, candidates, solutions, credits, max_credits);
        a.pop(candidate)

      }
    }
  } 

  const is_a_solution = (a, k, input) => (
    input.length === 1
    );

  const construct_candidates = (a, k, input) => {
    if (a.length === 0) {
      return input
    }
    else {
      return prune(input, a[k-2])
    }
  }  

  const process_solution = (a, k, input, solutions) => {
    solutions.push(JSON.parse(JSON.stringify(a)))
    //console.log(solutions)
    }

  const generateSchedule = (input) => {
    let a = []
    let credits = 0
    let max_credits = 20
    let solutions = []
    const sections2 = JSON.parse(JSON.stringify(input));
    backtrack(a, 0, sections2, solutions, credits, max_credits)
    return solutions;
  } 

  const overlap = (sec1, sec2) => {
    const days = ["monday", "tuesday", "wednesday", "thursday", "friday", "saturday", "sunday"];
    for (const meetSec1 of sec1.meetingsFaculty) {
      for (const meetSec2 of sec2.meetingsFaculty) {
        for (const day of days) {
          if (meetSec1.meetingTime[day] && meetSec2.meetingTime[day]) {
            if (meetSec1.meetingTime.beginTime <= meetSec2.meetingTime.endTime
              && meetSec1.meetingTime.endTime >= meetSec2.meetingTime.beginTime) {
                return true
              }
            break  
          }
        }
      }
    }
    return false
  }



  const prune = (arr, section) => {
    return(
    arr.filter((sec) => (sec.subjectCourse !== section.subjectCourse) &&
    !overlap(sec, section)
    ))}

  //console.log("solution:", generateSchedule(sections))
  if(sections.length > 0 ) {
    console.log("hola?")
    console.log("solutionss:", generateSchedule(sections.slice(0,10)))
  }

  return(
    <div class='par'>
      <Nav/>
      <ul>
        {sections.map(section => <li>{section.courseTitle}</li>)}
      </ul>
    </div>)
}

export default App;
