import './Schedule.css'
import colors from '../colors.json'
import {useState, useEffect} from 'react'

const Schedule = ({schedules, setSchedules}) => {
    const [n, setN] = useState(0)
    const days = ["monday", "tuesday", "wednesday", "thursday", "friday", "saturday", "sunday"]
    let styles = {}

    let schedule = schedules[n]
    schedule.forEach((section, idx) => {
        section.meetingsFaculty.forEach((meet, idx2)=>{
            for (let day of days) {
                if (meet.meetingTime[day]) {
                    let hour_count = meet.meetingTime.beginTime + 50
                    let i = 0
                    do {
                    if (i === 0)
                        styles[day+'-'+meet.meetingTime.beginTime] = {backgroundColor:colors[idx]}
                    else
                        styles[day+'-'+meet.meetingTime.beginTime] = {display:"none"}
                    hour_count += 50
                    i += 1
                    } while(hour_count < meet.meetingTime.endTime)
                }
            }
        })
    });
    
    const headersh = ["Hour", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"]
    const hourto12 = (num) => ( (num % 12 || 12) + ':00 ' + (num >= 12 ? 'p. m.' : 'a. m.' ));
    const hours = Array.from(Array(14), (e,i)=>hourto12(i+7))
    console.log(schedules)

    return (
        <table className='schedule ff-primary'>
            
            <caption className='scheduleTitle ff-primary'>Schedule</caption>
            <tr>
                {headersh.map(header => <th className='headersh'>{header}</th>)}
            </tr>
        {hours.map((hr, idx) =>
            <tr>
                <th className='headersv'>{hr}</th>
                {headersh.slice(1).map((header, idx2) => {
                    let hour24 = idx+7;
                    if (schedules.length > 0) {
                    for (let meet of schedules[n][0]["meetingsFaculty"])
                    {
                        console.log(meet)
                        if (meet.meetingTime[header.toLowerCase()] && (+ meet.meetingTime.beginTime.slice(0,2)) === hour24) {
                            return (<td className='cell filled'></td>)
                        }

                        else {
                            return (<td className='cell normal'></td>)
                        }
                    }
                } else {
                    return (<td className='cell normal'></td>)
                }
                    
                })
                
        }
            </tr>
                
                
        )}
            
            
        </table>
    )



}

export default Schedule
