import './Form.css'
import CourseForm from './CourseForm';
import {useState, useEffect} from 'react';
import bannerService from '../banners'

const Form = () => {

    const [term, setTerm] = useState(0)
    const [nforms, setNforms] = useState(1)
    const [terms, setTerms] = useState([])
    const [subjects, setSubjects] = useState({})
    const [courses, setCourses] = useState({})
    
    const handleClick = () => {
        setNforms(nforms+1)
    }

    const handleSubmit = () => {

    }

    useEffect(() => {
        bannerService.getTerms().then(
            (term) => setTerms(term.data))
      }, [])
    
    return (
    <div className='left-bar'>
        <label className="ff-primary" for="term">Select period:</label>
        <select className="ff-primary" name="term" id="term">
            {terms.map((term) =>
            <option value={term.code}>{term.description}</option>)}
        </select>
        {Array.from({length: nforms}).map((d, idx) => 
        <CourseForm courses={courses} 
        setCourses={setCourses} idx={idx} subjects={subjects} setSubjects={setSubjects} className="course-form"/>)}
        <button onClick={handleClick} className='button ff-primary'>+</button>
        <button onClick={handleSubmit} className='button ff-primary'>Generate Schedules</button>
    </div>
    )
}

export default Form;
