import {useState, useEffect} from 'react'
import axios from 'axios'
import bannerService from './banners'
import Nav from './components/Nav'


const App = () => {

  const [sections, setSections] = useState([])

  useEffect(() => {
    bannerService.classSearch(202320, 'ISCO')
      .then(sec => setSections(sec.data.data))
  }, [])

  const backtrack = (a, k, input, solutions) => {
    const c = []
    let nc;
    let i;

    if(is_a_solution(a, k, input)) {
      process_solution(a, k, input, solutions);
    } else {
      k = k + 1;
      construct_candidates(a, k, input, c, nc);
      for (i = 0; i < nc; i++) {
        a[k] = c[i];
        if (i === 0) {
          const chosenSection = input[k-1]
          const prunedArray  =  prune(input, chosenSection)
          backtrack(a, k, prunedArray, solutions);    
        }
        else {
        //make_move(a, k, input);
        backtrack(a, k, input, solutions);
        //unmake_move(a, k, input);
        }
      }
    }
  } 

  const is_a_solution = (a, k, input) => (
    k === input.length);

  const construct_candidates = (a, k, input, c, nc) => {
    c[0] = true;
    c[1] = false;
    nc = 2;
  }  

  const process_solution = (a, k, input, solutions) => {
    solutions.append(input.filter((d, ind) => a[ind+1]))
    }

  const generateSchedule = (input) => {
    let a = []
    let solutions = []
    const sections2 = JSON.parse(JSON.stringify(input));
    backtrack(a, 0, sections2, solutions)
    return solutions;
  } 

  const overlap = (sec1, sec2) => {
    const days = ["monday", "tuesday", "wednesday", "thursday", "friday", "saturday", "sunday"];

    for (const meetSec1 in sec1.meetingsFaculty) {
      for (const meetSec2 in sec2.meetingsFaculty) {
        for (const day in days) {
          if (meetSec1.meetingTime[day] && meetSec2.meetingTime[day]) {
            if (meetSec1.meetingTime.beginTime <= meetSec2.meetingTime.endTime
              && meetSec1.meetingTime.endTime >= meetSec2.meetingTime.beginTime) {
                return true
              }
            break  
          }
        }
      }
      return false
    }
  }

  const prune = (arr, section) => (
    arr.filter((sec) => (sec.subjectCourse !== section.subjectCourse) &&
    !overlap(sec, section)
    ))


  return(
    <div class='par'>
      <Nav/>
      <ul>
        {sections.map(section => <li>{section.courseTitle}</li>)}
      </ul>
    </div>)
}

export default App;
